const bodyEl=document.querySelector("body"),burger=document.querySelector(".js-burger"),menu=document.querySelector(".js-menu-nav");function init(){$.getJSON("goods.json",goodsOut)}function goodsOut(t){console.log(t);let e="",a={};if(localStorage.getItem("later")){a=JSON.parse(localStorage.getItem("later"));for(let o in a)e+=`<div class="cart">\n                        <p class="cart__name">${t[o].name}</p>\n                        <picture><source srcset="img/${t[o].imgwebp}" type="image/webp"><img class="cart__image" src="img/${t[o].img}" alt="${t[o].name}"></picture>\n                        <div class="cart__cost">${t[o].cost} грн</div>\n                        <a class="cart__link" href="goods.html#${o}">Просмотреть</a>\n                    </div>`;$(".goods-out").html(e)}else e+='<a class="cart__link-catalog" href="index.html#catalog">Добавьте товар.</a>',$(".goods-out").html(e)}function loadCart(){localStorage.getItem("cart")&&(cart=JSON.parse(localStorage.getItem("cart")),showMiniCart())}function showMiniCart(){let t="";t+=Object.keys(cart).reduce((t,e)=>t+cart[e],0),$(".mini-cart").html(t)}function showMenu(){menu.classList.toggle("active"),bodyEl.classList.toggle("active")}$("document").ready((function(){init(),loadCart(),burger.addEventListener("click",showMenu)}));