function init(){$.getJSON("goods.json",goodsOut)}function goodsOut(t){console.log(t);let e="",o={};if(localStorage.getItem("later")){o=JSON.parse(localStorage.getItem("later"));for(let a in o)e+=`<div class="cart">\n                        <p class="cart__name">${t[a].name}</p>\n                        <picture><source srcset="img/${t[a].imgwebp}" type="image/webp"><img class="cart__image" src="img/${t[a].img}" alt="${t[a].name}"></picture>\n                        <div class="cart__cost">${t[a].cost}</div>\n                        <a href="goods#${a}">Просмотреть</a>\n                    </div>`;$(".goods-out").html(e)}else $(".goods-out").html("Добавьте товар.")}$("document").ready((function(){init()}));